import './App.css';

function App() {
    const size = 10;
    var campo = new Array(size);
    var aux, i, j, k;

    for (var i = 0; i < campo.length; i++) {
        campo[i] = new Array(10).fill(0);
    }

    for (i = 0; i < 10; i++) {

        while (k != 10) {

            aux = Math.floor(Math.random() * 100);

            k = 0;

            for (j = 0; j <= i; j++) {

                if (aux == rand_num[j]) {
                    k = 1;
                }

                else if (aux != rand_num[j] && k == 0) {
                    k = 10;

                }

            }

        }


        rand_num[i] = aux;
        k = 0;
    }

    return campo.map(linha => {
        return (
            <>
                <p>
                    {linha.map(item => item + " ")}
                </p>

            </>
        )

    })

